from eth2spec.test.context import (
    expect_assertion_error,
    only_generator,
    single_phase,
    spec_test,
    with_phases,
)
from eth2spec.test.helpers.constants import FULU
from eth2spec.test.utils.kzg_tests import (
    bls_add_one,
    CELL_RANDOM_VALID2,
    encode_hex_list,
    INVALID_G1_POINTS,
    INVALID_INDIVIDUAL_CELL_BYTES,
    VALID_BLOBS,
)
from tests.infra.manifest import manifest
from tests.infra.template_test import template_test


def _run_verify_cell_kzg_proof_batch_test(
    spec, commitments, cell_indices, cells, proofs, expected_result=None, valid: bool = True
):
    if valid:
        result = spec.verify_cell_kzg_proof_batch(commitments, cell_indices, cells, proofs)
        if expected_result is not None:
            assert result == expected_result
    else:
        expect_assertion_error(
            lambda: spec.verify_cell_kzg_proof_batch(commitments, cell_indices, cells, proofs)
        )
        result = None

    yield (
        "data",
        "data",
        {
            "input": {
                "commitments": encode_hex_list(commitments),
                "cell_indices": cell_indices,
                "cells": encode_hex_list(cells),
                "proofs": encode_hex_list(proofs),
            },
            "output": result,
        },
    )


