@template_test
def _verify_cell_kzg_proof_batch_case_valid(blob_index):
    blob = VALID_BLOBS[blob_index]

    @manifest(preset_name="general", suite_name="kzg-mainnet")
    @only_generator("too slow")
    @with_phases([FULU])
    @spec_test
    @single_phase
    def the_test(spec):
        cells, proofs = spec.compute_cells_and_kzg_proofs(blob)
        commitments = [spec.blob_to_kzg_commitment(blob) for _ in cells]
        cell_indices = list(range(spec.CELLS_PER_EXT_BLOB))

        yield from _run_verify_cell_kzg_proof_batch_test(
            spec,
            commitments,
            cell_indices,
            cells,
            proofs,
            expected_result=True,
        )

    return (the_test, f"test_verify_cell_kzg_proof_batch_case_valid_{blob_index}")


for blob_index in range(len(VALID_BLOBS)):
    _verify_cell_kzg_proof_batch_case_valid(blob_index)


