def test_verify_cell_kzg_proof_batch_case_incorrect_commitment(spec):
    cells, proofs = spec.compute_cells_and_kzg_proofs(VALID_BLOBS[5])
    cells, proofs = cells[:1], proofs[:1]
    # Use the wrong commitment
    commitments = [bls_add_one(spec.blob_to_kzg_commitment(VALID_BLOBS[5]))]
    cell_indices = list(range(len(cells)))

    yield from _run_verify_cell_kzg_proof_batch_test(
        spec,
        commitments,
        cell_indices,
        cells,
        proofs,
        expected_result=False,
    )


